public class OdNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Print only odd numbers betwen 1 to 20");
		for (int i = 0; i < 21; i++) {
			if (i % 2 != 0) {
				System.out.println(i);
			}

		}

	}
}
